import React from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";

import Header from "../../components/Header";

import check from '../../asset/bannerCheck.png';

export default function PricingInfo() {
    const navigate = useNavigate();
    const price = 18000;

    const onClickBasic = () => {
        navigate('/payment/basic');
    }

    const onClickEnterprise = () => {
        navigate('/payment/enterprise');
    }

    return(
        <Container>
            <Header />
            <TextArea>
                <Title>
                    Í∞ÄÏû• Ìï©Î¶¨Ï†ÅÏù∏ Í∞ÄÍ≤©Ïùò{'\n'}
                    GLOT(General Logic of Text) Ïù∏Í≥µ ÏßÄÎä•(AI)ÏúºÎ°ú{'\n'}
                    ÏµúÍ≥†Í∞Ä ÎêòÏñ¥Î≥¥ÏÑ∏Ïöî.
                </Title>
            </TextArea>
            <BannerArea>
            <Banner>
                        <BannerTitle
                            style={{
                                backgroundColor: 'rgba(50, 144, 255, 1)',
                                color: 'rgba(255, 255, 255, 1)'
                            }}
                        >
                            GLOT Î≤†Ïù¥ÏßÅ
                        </BannerTitle>
                        <BannerPrice>
                            {new Intl.NumberFormat().format(price)}<Small style={{ fontSize: '28px', lineHeight: '32px', marginBottom: '3px'}}>Ïõê</Small><Gray>/Ïõî</Gray>
                        </BannerPrice>
                        <BannerContent>
                            <Check src={check} />ÏûëÎ¨∏ ÏûÖÎ†• <Small>(Í∏ÄÏûê Ïàò Î¨¥Ï†úÌïú)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />GLOT AI ÏûëÎ¨∏ ÏΩîÏπ≠ Ï†úÍ≥µ{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />ÏûëÎ¨∏ ÎÇ¥Ïó≠ Î©îÎ™®Î¶¨ Ï†úÍ≥µ <Small>(ÏµúÍ∑º 100Í±¥ Î≥¥Í¥Ä)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />ÏûëÎ¨∏ ÎÇ¥Ïó≠ Î¨∏ÏÑú Îã§Ïö¥Î°úÎìú <Small>(DOC, PDF, JPG, TXT)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />ÏûëÎ¨∏ ÎÇ¥Ïó≠ Ïù∏ÏáÑ Í∏∞Îä• Ï†úÍ≥µ
                        </BannerContent>
                        <ButtonContent>
                            <CheckButton
                                style={{
                                    backgroundColor: 'rgba(50, 144, 255, 1)',
                                }}
                                onClick={onClickBasic}
                            >
                                GLOT Î≤†Ïù¥ÏßÅ Íµ¨Îß§ÌïòÍ∏∞
                            </CheckButton>
                        </ButtonContent>
                    </Banner>
                    <Banner>
                        <BannerTitle
                            style={{
                                backgroundColor: 'rgba(255, 102, 102, 1)',
                                color: 'rgba(255, 255, 255, 1)'
                            }}
                        >
                            GLOT ÏóîÌÑ∞ÌîÑÎùºÏù¥Ï¶à
                        </BannerTitle>
                        <BannerPrice>
                            {new Intl.NumberFormat().format(price*0.8)}<Small style={{ fontSize: '28px', lineHeight: '32px', marginBottom: '3px'}}>Ïõê</Small><Gray>/Ïõî, Ïú†Ï†ÄÎãπ</Gray>
                        </BannerPrice>
                        <BannerContent>
                            <Bold
                                style={{
                                    fontSize: '20px',
                                }}
                            >
                                Everything in Plus.
                            </Bold>{'\n'}
                            {'\n'}
                            <Check src={check} />üü¢ÍµêÏú° Í∏∞Í¥Ä Í∞ÄÏûÖ ÏöîÍ∏àÏ†ú{'\n'} 
                            <Small>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(10Í∞ú Ïù¥ÏÉÅÏùò Í≥ÑÏ†ï Îã®Ï≤¥ Îì±Î°ù Ïãú ÌòúÌÉù Ï†ÅÏö©)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />ÍµêÏú° Ï†ÑÏö© Í≥ÑÏ†ï Ìï†Ïù∏ Ï†ÅÏö©{'\n'}
                            <Small>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Ïõî Í∏∞Ï§Ä 20%, Ïó∞ Í∏∞Ï§Ä 30% ÌòúÌÉù Ï†ÅÏö© - %Îäî ÏûÑÏùò)</Small>
                        </BannerContent>
                        <ButtonContent>
                            <CheckButton
                                style={{
                                    backgroundColor: 'rgba(255, 102, 102, 1)',
                                }}
                                onClick={onClickEnterprise}
                            >
                                GLOT ÏóîÌÑ∞ÌîÑÎùºÏù¥Ï¶à Íµ¨Îß§ÌïòÍ∏∞
                            </CheckButton>
                        </ButtonContent>
                    </Banner>
            </BannerArea>
        </Container>
    )
}

const Container = styled.div`
    width: 100%;
    height: 100%;
    background-color: #FFFFFF;
`;

const TextArea = styled.div`
    width: 100%;
