    align-items: center;
    margin-top: 53px;
    margin-bottom: 96px;
`;

const Banner = styled.div`
    width: 385px;
    height: 560px;
    border-radius: 24px;
    background-color: rgba(255, 255, 255, 1);
    margin: 0 18px;
    box-shadow: 0px 5px 60px 0px rgba(62, 96, 130, 0.16);
`;

const BannerTitle = styled.div`
    width: 100%;
    height: 64px;
    font-size: 24px;
    font-weight: 600;
    line-height: 33.6px;
    letter-spacing: -0.03em;
    text-align: center;
    color: rgba(17, 17, 17, 1);
