    font-size: 18px;
    font-weight: 500;
    line-height: 18px;
    letter-spacing: -0.03em;
    color: rgba(195, 202, 211, 1);
    margin-left: 3px;
    margin-bottom: 6px;
`;

const ButtonContent = styled.div`
    width: calc(100% - 48px);
    height: 56px;
    padding: 24px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const CheckButton = styled.div`
