import React, {useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useQuery } from 'react-query';
import { useRecoilValue } from 'recoil';
import { UserType } from '../../recoil/Atom';

import Header from '../../components/Header';
import StopModal from '../../components/Modal/StopSubscribeModal';
import RefundModal from '../../components/Modal/RefundModal';
import Backdrop from '../../components/Modal/Backdrop';

import PaidInfoApi from '../../services/PaidInfo';
import StopApi from '../../services/StopSubscription';

import recent from '../../asset/recentIcon.png';
import price from '../../asset/subscribeIcon.png';

export default function PaidInformation() {
    const navigate = useNavigate();

    const userType = useRecoilValue(UserType);
    const [isStopModalOpen, setIsStopModalOpen] = useState(false);
    const [isRefundModalOpen, setIsRefundModalOpen] = useState(false);
    const [lastDate, setLastDate] = useState('');

    const { data, isLoading, error } = useQuery('PaidInfo', () => PaidInfoApi(),{
        onSuccess: (data) => {
            console.log(data);
        },
        onError: (error) => {
            console.log(error);
        }
    });

    const onClickMyInfo = () => {
        navigate('/mypage');
    }

    const onClickGroupInfo = () => {
        if(userType === 'SUBSCRIBED') {
            navigate('/mypage/groupinfo');
        }
        else {
            alert('요금제 구매 후 이용 가능합니다.');
        }
    }


    const onClickPricePlan = () => {
        navigate('/mypage/paid_info');
    }

    const onClickStop = () => {
        setIsStopModalOpen(true);
    }

    const onClickRefund = () => {
        setIsRefundModalOpen(true);
    }

    const dummy = [
        {
            paidDate: '2021년 10월 13일',
            duration: '2023년 9월 14일 ~ 2023년 10월 13일',
            cardNumber: '신용카드 ****-****-****-1234',
            amount: 10000,
            surtax: 1000,
            status: '주문 완료',
        },
    ]
    const tempt = '2023년 9월 14일 ~ 2023년 10월 13일'

    const parts = tempt.split("~");
    if (parts.length === 2) {
        const lastDate = parts[1].trim();
        setLastDate(lastDate);
    } else {
        console.error("올바른 형식이 아닙니다.");
    }

    return (
        <Container>
            <Header />
            <Content>
                <SideContent>
                    <SideTitle>마이페이지</SideTitle>
                    <Menu onClick={onClickMyInfo}>내 정보</Menu>
                    <Menu onClick={onClickGroupInfo}>구매 계정 정보</Menu>
                    <Menu onClick={onClickPricePlan}><Bold>결제 정보</Bold></Menu>
                </SideContent>
                {/* <InnerContent>
                    <ContentOuter>
                        <TitleArea>
                            <Title>
                                <Blue>{data.plan}&nbsp;</Blue> 구독중
                                <Icon
                                    src={price}
                                    style={{
                                        width: '31px',
                                        height: '30.14px',
                                        marginLeft: '8px'
                                    }}
                                />
                            </Title>
                            <Descriptions>
                                {data.plan} {data.firstPaidDate}부터 시작
                            </Descriptions>
                        </TitleArea>
                        <ContentArea>
                            <div
                                style={{
                                    display: 'flex',
                                    flexDirection: 'row',
                                }}
                            >
                                <TextArea>
                                    <Line>
                                        <Text>
                                            <Name>
                                                결제일
                                            </Name>
                                            <Value>
                                                매월 <Blue>&nbsp;{data.payPeriod}</Blue>일
                                            </Value>
                                        </Text>
                                    </Line>
                                    <Line>
                                        <Text>
                                            <Name>
