import React from "react";
import styled from "styled-components";

import mainLogo from "../../asset/mainLogo.png";

export default function MainFooter() {
    return (
        <Container>
            <Content>
                <Logo>
                    <img src={mainLogo} alt="logo" width="153.56px" height="20px" />
                </Logo>
                <Description>
                    <Title ref='https://rhetorike.notion.site/ff6b3077e9124da1b5d61684c86c9f66'>
                        개인정보처리방침
                    </Title>
                    <Script>
                        상호 : (주)레토리케&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;대표 : 김상진, 김세현{"\n"}
                        주소 : 서울특별시 종로구 세종대로 23길 47, 603-451호{"\n"}
                        사업자 등록번호 : 670-86-02140{"\n"}
                        {"\n"}
                        Copyright(c) 2023 Rhetorike corp. All Rights reserved.
                    </Script>
                </Description>
            </Content>
        </Container>
    );
    }



const Container = styled.div`
    width: 100%;
    height: 407px;
    display: flex;
    justify-content: center;
    text-align: center;
`;

const Content = styled.div`
    display: flex;
    justify-content: center;
    margin-top: 64px;
    margin-right: 380px;
    @media (max-width: 1010px) {
        margin-right: 0px;
    }
    @media (max-width: 768px) {
        flex-direction: column;
        margin-right: 0px;
        margin-top: 0px;
    }
`;

const Logo = styled.div`
    display: flex;
    font-size: 17px;
    font-weight: 700;
    margin-right: 57.44px;
`;

const Description = styled.div`
    width: 330px;
    height: 146px;
    display: flex;
    flex-direction: column;
    text-align: left;
    @media (max-width: 768px) {
        width: 100%;
        margin-top: 30px;
    }
`;

const Title = styled.a`
    width: 110px;
    font-size: 15px;
    font-weight: 700;
    border: none;
    border-bottom: 1px solid rgba(151, 152, 154, 1); // 밑줄 색상을 글자색과 동일하게 설정
    color: rgba(151, 152, 154, 1);
    cursor: pointer;
`;

const Script = styled.div`
    font-size: 13px;
    font-weight: 400;
    margin-top: 20px;
    white-space: pre-line;
    line-height: 2;
    color: rgba(151, 152, 154, 1);
`;
