import Slider from "react-slick";
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import styled from "styled-components";
import React from 'react';

import slideImg1 from '../../asset/slide1.png';
import slideImg2 from '../../asset/slide2.png';
import slideImg3 from '../../asset/slide3.png';
import slideImg4 from '../../asset/slide4.png';
import slideImg5 from '../../asset/slide5.png';

export default function MainCarousal() {
    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 3, // 한 번에 보여질 슬라이드 개수
        autoplay: false,
        slidesToScroll: 2,
        centerMode: true, // 중앙 정렬 모드 활성화
        centerPadding: '0px', // 중앙 정렬 시 좌우 간격 조절
        // prevArrow: <CustomPrevArrow />, // 이전 버튼 컴포넌트
        // nextArrow: <CustomNextArrow />, // 다음 버튼 컴포넌트
    };

    return (
        <MainCarousalContainer>
            <Slider {...settings}>
                <CarousalContent>
                    <Image
                        src={slideImg1}
                    />
                </CarousalContent>
                <CarousalContent>
                    <Image
                        src={slideImg2}
                    />
                </CarousalContent>
                <CarousalContent>
                    <Image
                        src={slideImg3}
                    />
                </CarousalContent>
                <CarousalContent>
                    <Image
                        src={slideImg4}
                    />
                </CarousalContent>
                <CarousalContent>
                    <Image
                        src={slideImg5}
                    />
                </CarousalContent>
                <CarousalContent>
                    <Image
                        src={slideImg5}
                    />
                </CarousalContent>
            </Slider>
        </MainCarousalContainer>
    );
}

const MainCarousalContainer = styled.div`
    width: 100%;
    margin-bottom: 104px;
    border: 1px solid black;
`;

const CarousalContent = styled.div`
    width: 100%;
    outline: none; /* 포커스 시 테두리 제거 */
`;

const Image = styled.img`
    width: 588px;
    height: 414px;
    object-fit: cover;
`;

const CustomPrevArrow = styled.div`
    position: absolute;
    top: 50%; 
    left: 0;
    background-color: black;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
`;

// 다음 버튼 컴포넌트
const CustomNextArrow = styled.div`
    position: absolute;
    background-color: black;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
`;
