    text-align: left;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 6px;
    padding-left: 16px;
    &:disabled {
        background-color: #FFF;
    }
`;

const ButtonArea = styled.div`
    position: relative;
    bottom: 31px;
    left: 300px;
    width: 100px;
    height: 10px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const EditButton = styled.div`
    font-size: 16px;
    font-weight: 400;
    color: rgba(50, 144, 255, 1);
    cursor: pointer;
    margin-right: 16px;
`;

const BottomArea = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: flex-end;
    margin-top: 20px;
`;

const Button = styled.div`
    font-size: 16px;
    font-weight: 600;
    color: rgba(50, 144, 255, 1);
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
`;
