import React from 'react';
import {useState} from 'react';
import styled from 'styled-components';

export default function WritingTest() {
    const [writingTitle, setWritingTitle] = useState('');
    const [writingContent, setWritingContent] = useState('');

    const handleContentChange = (e) => {
        setWritingContent(e.target.value);
    }

    return (
        <Container>
            <InnerContainer>
                <WritingArea>
                    <AreaWrapper>
                    <TitleArea>
                        <TitleInput
                            type="text"
                            placeholder="제목을 입력해주세요"
                            value={writingTitle}
                            onChange={(e) => setWritingTitle(e.target.value)}
                        />
                    </TitleArea>
                    <ContentArea>
                        <ContentInput
                            placeholder="문장을 입력하여 작문을 시작해보세요!"
                            value={writingContent}
                            onChange={handleContentChange}
                        />
                    </ContentArea>
                    </AreaWrapper>
                </WritingArea>
                <ButtonArea>
                    <SaveButton>저장</SaveButton> 
                </ButtonArea>
            </InnerContainer>
        </Container>
    );
}

const Container = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
`;

const InnerContainer = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    transition: all 0.3s ease-in-out;
`;

const WritingArea = styled.div`
    width: 598px;
    height: 100%;
    background-color: #FFFFFF;
    border: none;
    display: flex;
    flex-direction: column;
    padding: 48px;
    margin-top: 24px;
    overflow: auto;
    animation: 0.3s ease-in-out;

    // 부모 요소에 스크롤 스타일 적용
    display: flex;
    flex-direction: column;
    height: 100%;
    overflow: auto;
`;

const AreaWrapper = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const TitleArea = styled.div`
    width: 100%;
    height: 50px;
    outline: none;
    margin-bottom: 20px;
`;

const TitleInput = styled.input`
    width: 100%;
    height: 100%;
    border: none;
    outline: none;
    font-size: 30px;
    font-weight: 600;
    &::placeholder {
        color: rgba(218, 219, 221, 1);
    }
`;

const ContentArea = styled.div`
    width: 100%;
    height: 100%;
`; 

const ContentInput = styled.textarea`  
    width: 100%;
    height: 100%;
    border: none;
    outline: none;
    font-size: 16px;
    font-weight: 400;
    line-height: 28.8px;
    resize: none;
    white-space: pre-line;
    &::placeholder {
        color: rgba(218, 219, 221, 1);
    }
`;

const ButtonArea = styled.div`
    width: 100%;
    height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const SaveButton = styled.button`
    width: 100px;
    height: 40px;
    border: none;
    border-radius: 8px;
    background-color: #3290ff;
    color: #FFFFFF;
    font-size: 18px;
    font-weight: 700;
    cursor: pointer;
`;
