import React from 'react';
import {useRef, useEffect, useState} from 'react'
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import styled from 'styled-components';

export default function WritingTest() {
    const [writingTitle, setWritingTitle] = useState('');
    const [writingContent, setWritingContent] = useState('');
    const ref = useRef();

    const handleContentChange = (e) => {
        setWritingContent(e.target.value);
    }

    const makePdf = () => {
        const converToImg = async () => {
            // html to imageFile
            const paper = document.querySelector("#writing");

            const canvas = await html2canvas(paper);
            const imageFile = canvas.toDataURL("image/png", 1.0);

            return imageFile
        }

        const converToPdf = (imageFile) => {
            // imageFile to pdf

            const doc = new jsPDF("p", "mm", "a4");

            const pageWidth = doc.internal.pageSize.getWidth();
            const pageHeight = doc.internal.pageSize.getHeight();

            doc.addImage(imageFile, "JPEG", 0, 0, pageWidth, pageHeight);

            doc.save("test.pdf")

            window.open(doc.output("bloburl"))

            const pdf = new File([doc.output("blob")], "test.pdf", {
                type: "application/pdf",
            });

            return pdf
        }

        return {
            viewWithPdf: async () => {
                // html to imageFile
                const imageFile = await converToImg()

                // imageFile to Pdf
                const pdf = converToPdf(imageFile)
            }
        }
    }

    return (
        <Container>
            <InnerContainer>
                <WritingArea id='writing'>
                    <TitleArea>
                        <TitleInput
                            type="text"
                            placeholder="제목을 입력해주세요"
                            value={writingTitle}
                            onChange={(e) => setWritingTitle(e.target.value)}
                        />
                    </TitleArea>
                    <ContentArea>
                        <ContentInput
                            placeholder="문장을 입력하여 작문을 시작해보세요!"
                            value={writingContent}
                            onChange={handleContentChange}
                        />
                    </ContentArea>
                </WritingArea>
                <ButtonArea>
                    <SaveButton onClick={makePdf}>저장</SaveButton> 
                    <SaveButton>출력</SaveButton>
                </ButtonArea>
            </InnerContainer>
        </Container>
    );
}

const Container = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
`;

const InnerContainer = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    transition: all 0.3s ease-in-out;
`;

const WritingArea = styled.div`
    height: 842px;
    width: 595px;
    margin: 30px;
    background-color: white;
    text-align: center;
    background-color: #FFFFFF;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    overflow-y: scroll;
`;

const TitleArea = styled.div`
    width: 100%;
    height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const TitleInput = styled.input`
    width: 80%;
    height: 40px;
    font-size: 24px;
    font-weight: 700;
    border: none;
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    &:focus {
        outline: none;
    }
`;

const ContentArea = styled.div`
    width: 100%;
    height: 100%;
    min-height: 500px;
    display: flex;
    justify-content: center;
    margin-top: 50px;
`;

const ContentInput = styled.textarea`
    width: 80%;
    font-size: 18px;
    font-weight: 500;
    border: none;
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    resize: none;
    &:focus {
        outline: none;
    }
`;

const ButtonArea = styled.div`
    width: 100%;
    height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const SaveButton = styled.button`
    width: 100px;
    height: 40px;
    border: none;
    border-radius: 8px;
    background-color: #3290ff;
    color: #FFFFFF;
    font-size: 18px;
    font-weight: 700;
    cursor: pointer;
`;
