import React,{ useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { useQuery } from "react-query";

import Header from "../../components/Header";
import PricingModal from "../../components/Modal/PricingMenuModal";

import MyInfoApi from "../../services/MyInfo";
import BasicOrderApi from "../../services/BasicOrder";

import EnterpriseOrderApi from "../../services/EnterpriseOrder";
import StopApi from "../../services/StopSubscription";

import open from "../../asset/openIcon.png";

export default function BasicPricePlan() {
    const navigate = useNavigate();
    const modalRef = useRef();

    const Price = 18000;

    const MonthlyPrice = new Intl.NumberFormat().format(Price);
    const YearlyPrice = new Intl.NumberFormat().format(Price * 0.7);
    const SavePrice = new Intl.NumberFormat().format((Price * 12) - (Price * 0.7 * 12));

    const Vat = new Intl.NumberFormat().format(Price * 0.7 * 12 * 0.1);
    const TotalPrice = new Intl.NumberFormat().format(Price * 0.7 * 12);
    const FinalPrice = new Intl.NumberFormat().format(Price * 0.7 * 12 * 1.1);

    const MonthlyVat = new Intl.NumberFormat().format(Price * 0.1);
    const MonthlyTotalPrice = new Intl.NumberFormat().format(Price);
    const MonthlyFinalPrice = new Intl.NumberFormat().format(Price * 1.1);

    const [userNumber, setUserNumber] = useState(1);
    const [openMenu, setOpenMenu] = useState(false); // 결제 방식 선택
    const [pricingType, setPricingType] = useState('perYear');
    const [isCheck, setIsCheck] = useState(false); //약관 동의
    const [payment, setPayment] = useState({}); //결제 정보
    const [expiryMonth, setExpiryMonth] = useState('');
    const [expiryYear, setExpiryYear] = useState('');
    const [clickCoordinates, setClickCoordinates] = useState({ x: 0, y: 0 }); // 클릭 좌표

    const { data }  = useQuery('myInfo', () => MyInfoApi(),{
        onError: (error) => {
            console.log(error);
        },
    });

    // Check if data exists before accessing properties
    const userName = data?.name || '';
    const userEmail = data?.email || '';
    const userPhone = data?.phone || '';

    // 모달 창 외부 클릭 시 모달 닫기
    // useEffect(() => {
    //     const handleClickOutside = (e) => {
    //         if(openMenu && (!modalRef.current.contains(e.target)))
    //             setOpenMenu(false);
    //     }
    //     document.addEventListener('mousedown', handleClickOutside);

    //     return () => document.removeEventListener('mousedown', handleClickOutside);
    // }, [openMenu]);

    // const handleClick = (e) => {
    //     // 클릭한 좌표 저장
    //     setClickCoordinates({ x: e.clientX, y: e.clientY });

    //     // 모달 열기
    //     setOpenMenu(true);
    //     console.log(clickCoordinates);
    // };

    const onClickOrder = async () => {
        try {
            const expiry = `${expiryYear}-${expiryMonth}`;

            const finalData = {
                ...payment,
                expiry,
            }
            const response = await BasicOrderApi(pricingType, finalData);
        }
        catch (error) {
            console.log(error);
        }
    }

    return (
        <Container>
            <Header />
            <Content>
                <InnerContent>
                    <InnerContainer>
                        <TextArea>
                            <Title>
                                정기 결제 신청
                            </Title>
                            <Description>
                                처음 결제한 날짜를 정기 결제일로 자동 설정하며, 요금은{'\n'}
                                사용인원에 따라 결제금액이 변경될 수 있습니다.
                            </Description>
                        </TextArea>
                        <Box>
                            <LeftBox>
                                <BoxContainer>
                                    <BoxTitle>
                                        GLOT 베이직
                                    </BoxTitle>
                                    <BoxContent>
                                        <Name>
                                            사용자 수
                                        </Name>
                                        <div style={{
                                            display: 'flex',
                                            justifyContent: 'center',
                                            alignItems: 'center',
                                        }}>
                                            <UserInput
                                                value={userNumber}
                                                onChange={(e) => setUserNumber(e.target.value)}
                                                disabled={true}
                                            >                    
                                            </UserInput>
                                            <Name>
                                                명
                                            </Name>
                                        </div>
                                    </BoxContent>
                                    <Line />
                                    <BoxContent>
                                        <Name>
                                            월간 결제(Monthly)
                                        </Name>
                                        <Value>
                                            매월 <Big>{MonthlyPrice}</Big>원 납부
                                        </Value>
                                    </BoxContent>
                                    <Line />
                                    <BoxContent>
                                        <Name>
                                            연간 결제(Yearly)
                                        </Name>
                                        <Value>
                                            매월 <Blue>{YearlyPrice}</Blue>원 납부
                                        </Value>
                                    </BoxContent>
                                    <Notice>
                                    ※ 월간 결제, 연간 결제 모두 vat 별도 금액입니다.
                                    </Notice>
                                    <Line />
                                    <BoxContent
                                        style={{
                                            justifyContent: 'flex-start',
                                            height: '56px',
                                        }}
                                    >
                                        <Name
                                            onClick={(e) => {
                                                // handleClick(e);
                                                setOpenMenu(!openMenu);
                                            }}
                                            ref={modalRef} // ref 추가
                                        >
                                            {pricingType === 'perYear' ? '연간결제' : '월간결제'}
                                            <Icon src={open} />
                                        </Name>
                                        <Message
                                            style={{
                                                display: pricingType === 'perYear' ? 'block' : 'none',
                                            }}
                                        >
                                            축하합니다! {SavePrice}원을 절약할 수 있습니다
                                        </Message>                   
                                    </BoxContent>
                                    <BoxContent
                                        style={{
                                            height: '32px',
                                        }}
                                    >
                                        <Name
                                            style={{
                                                fontSize: '15px',
                                                fontWeight: '500',
                                                lineHeight: '21px',
                                            }}
                                        >
                                            총 결제 예정 금액
                                        </Name>
                                        <Value>
                                            {pricingType === 'perYear' ? (
                                                <><Big>{FinalPrice} </Big>/연</>
                                            ) : (
                                                <><Big>{MonthlyFinalPrice} </Big>/월</>
                                            )}
                                        </Value>
                                    </BoxContent>
                                    <BoxContent
                                        style={{
                                            height: '32px',
                                            marginTop: '8px',
                                        }}
                                    >
                                        <Name>
                                            공급가 액
                                        </Name>
                                        <Value>
                                            {pricingType === 'perYear' ? (
                                                <>{TotalPrice}원</>
                                            ) : (
                                                <>{MonthlyTotalPrice}원</>
                                            )}
                                        </Value>
                                    </BoxContent>
                                    <BoxContent
                                        style={{
                                            height: '32px',
                                            marginBottom: '20px',
                                        }}
                                    >
                                        <Name>
                                            세액
                                        </Name>
                                        <Value>
                                            {pricingType === 'perYear' ? (
                                                <>{Vat}원</>
                                            ) : (
                                                <>{MonthlyVat}원</>
                                            )}
                                        </Value>
                                    </BoxContent>
                                </BoxContainer>
                                <CheckBox>
                                    <input 
                                        type="checkbox" 
                                        id="checkbox" 
                                        checked={isCheck}
                                        onChange={() => setIsCheck(!isCheck)}
                                    />
                                    <Link
                                        href="https://rhetorike.notion.site/7fdf82818471443aa6746139a446ee43?pvs=4"
                                        target="_blank"
                                    >
                                        정기과금, 이용약관
                                    </Link>
                                    의 내용을 확인하였고 동의합니다.
                                </CheckBox>
                                <Button
                                    style={{
                                        backgroundColor : isCheck ? 'rgba(50, 144, 255, 1)' : 'rgba(183, 184, 186, 1)',
                                    }}
                                    disabled={!isCheck}
                                    onClick={onClickOrder}
                                >
                                    결제 신청
                                </Button>
                                <Notice>
