import React, { useEffect } from 'react';
import styled from 'styled-components';

export default function SaveNotice() {
  return (
    <Container>
        <Description>
            작성중인 내용이 있습니다. 저장하시겠습니까?
        </Description>
        <ButtonArea>
            <Button>저장</Button>
            <Button>저장 안 함</Button>
        </ButtonArea>
    </Container>
  );
}

const Container = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    width: 600px;
    height: 300px;
    display: flex;
    flex-direction: column;
    background-color: #ffffff;
    border-radius: 10px;
    padding: 20px;
    z-index: 999;
`;

const Description = styled.div`
    font-size: 18px;
    font-weight: 500;
    margin-bottom: 20px;
`;

const ButtonArea = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
`;

const Button = styled.button`
    width: 80px;
    height: 40px;
    border-radius: 10px;
    border: none;
    background-color: #ffffff;
    font-size: 16px;
    font-weight: 500;
    cursor: pointer;
    margin-left: 10px;
    &:hover {
        background-color: #F2F3F5;
    }
`;
