            })
            .catch((error) => {
                setIsLoading(false); // 에러 발생 시도 로딩 상태 해제
                console.log(error);
            });
    }, []);

    const onClickMyInfo = () => {
        navigate('/mypage');
    }

    const onClickGroupInfo = () => {
        if(userType === 'SUBSCRIBED') {
            navigate('/mypage/groupinfo');
        }
