        }
    }


    const onClickPricePlan = () => {
        navigate('/mypage/paid_info');
    }

    const onClickStop = () => {
        setIsStopModalOpen(true);
    }

    const onClickRefund = () => {
        setIsRefundModalOpen(true);
    }

    //paidInfo의 history에서 duration의 ~ 뒷부분만 가져오기
    const lastDate = paidInfo.history && paidInfo.history.map((item, index) => {
        return(
