import React, {useState} from 'react';
import styled from 'styled-components';
import { useNavigate, useLocation } from 'react-router-dom';

import mainLogo from '../../asset/mainLogoWhite.png';

export default function MainTopBar() {
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const location = useLocation();

    const onClickLogo = () => {
        navigate('/');
    }

    const onClickMenu = (menu) => {
        if(menu === 'About') {
            navigate('/');
        } else if(menu === 'GLOT AI') {
            navigate('/glotai');
        } else if(menu === 'Pricing') {
            navigate('/pricing');
        }
    }

    const isMenuActive = (menu) => {
        return (location.pathname === '/' && menu === 'About') ||
       (location.pathname === '/glotai' && menu === 'GLOT AI') ||
       (location.pathname === '/pricing' && menu === 'Pricing');
    }

    const onClickStart = () => {
        window.open("http://localhost:3000/writing", "_blank");
    }

    return (
        <MainTopBarOuter>
            <MainTopBarContainer>
                <MainMenuBar>
                    <MainMenuBarButton onClick={() => setIsMenuOpen(!isMenuOpen)}>
                        <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                            <path d="M3 12H21" stroke="#FFFFFF" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                            <path d="M3 6H21" stroke="#FFFFFF" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                            <path d="M3 18H21" stroke="#FFFFFF" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                    </MainMenuBarButton>
                </MainMenuBar>
                <MainTopBarLogo onClick={onClickLogo} src={mainLogo}/>
                <MainTopBarMenu>
                    <MainTopBarMenuList>
                        <MainTopBarMenuButton
                            onClick={() => onClickMenu('About')} 
                            active={isMenuActive('About')}
                            style={{
                                width: '100px',
                            }}
                        >
                            레토리케 소개
                        </MainTopBarMenuButton>
                        <MainTopBarMenuButton
                            onClick={() => onClickMenu('GLOT AI')} 
                            active={isMenuActive('GLOT AI')}
                            style={{
                                width: '70px',
                                marginLeft: '56px',
                            }}
                        >
                            GLOT AI
                        </MainTopBarMenuButton>
                        <MainTopBarMenuButton
                            onClick={() => onClickMenu('Pricing')} 
                            active={isMenuActive('Pricing')}
                            style={{
                                width: '65px',
                                marginLeft: '56px',
                            }}
                        >
                            Pricing
                        </MainTopBarMenuButton>
                    </MainTopBarMenuList>
                    <MainTopBarStartBtn onClick={onClickStart}>
                        글롯 시작하기
                    </MainTopBarStartBtn>
                </MainTopBarMenu>
            </MainTopBarContainer>
        </MainTopBarOuter>
    );
}

const MainTopBarOuter = styled.div`
    width: 100%;
    height: 80px; 
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MainTopBarContainer = styled.div`
    width: 80%;
    height: 80px;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    @media (max-width: 1010px) {
        width: 100%;
        height: 80px;
        display: flex;
        justify-content: flex-start;
        align-items: center;
    }
`;

const MainMenuBar = styled.div`
    width: 24px;
    height: 24px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 32px;
    @media (min-width: 1010px) {
        display: none;
    }
`;

const MainMenuBarButton = styled.button`
    width: 24px;
    height: 24px;
    border: none;
    background-color: transparent;
    cursor: pointer;
    margin-left: 32px;
`;

const MainTopBarLogo = styled.img`
    width: 155.65px;
    height: 20.27px;
    font-size: 30px;
    font-weight: bold;
    cursor: pointer;
    color: #ffffff;
    margin-left: 24px;
    @media (max-width: 591px) {
        display: none;
    }
`;

const MainTopBarMenu = styled.div`
    margin-left: 100px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
`;

const MainTopBarMenuList = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    @media (max-width: 1010px) {
        display: none;
    }
`;

const MainTopBarMenuButton = styled.div`
    font-size: 18px;
    font-weight: ${props => props.active ? 'bold' : 'normal'};
    cursor: pointer;
    color: #ffffff;
`;

const MainTopBarStartBtn = styled.button`
    width: 133px;
    height: 46px;
    border: none;
    border-radius: 10px;
    background-color: rgba(50, 144, 255, 1);
    color: #FFFFFF;
    font-size: 17px;
    font-weight: 700;
    cursor: pointer;
    @media (max-width: 1010px) {
        display: none;
    }
`;
