import React,{useState, useEffect} from "react";
import styled from "styled-components";

import IdSearchMobileApi from "../../services/IdSearchMobile";

function ResultModal(props) {
    const idlist = props.searchResult;
    const length = 10;

    const closeInfoModal = () => {
        props.setIsResult(false);
    };

    useEffect(() => {
        IdSearchMobileApi()
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.log(error);
            });
    }
    , []);

    return (
        <ModalContainer>
            <Title>
                아이디 찾기 결과
            </Title>
            <Description>
                입력하신 정보로 가입된 아이디 <Number>{length}건</Number>을 찾았습니다.
            </Description>
            <IdList>
            </IdList>
            <ButtonArea>
                <Button onClick={closeInfoModal}>
                    확인
                </Button>
            </ButtonArea>
        </ModalContainer>
    );
}

const ModalContainer = styled.div`
    position: absolute;
    top: 40%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 418px;
    height: 270px;
    background-color: #fff;
    border-radius: 8px;
    z-index: 999;
    padding: 30px;
`;

const Title = styled.div`
    font-size: 24px;
    font-weight: 700;
`;

const Description = styled.div`
    font-size: 16px;
    font-weight: 500;
    color: #97989a;
    margin-top: 10px;
`;

const Number = styled.span`
    font-size: 16px;
    font-weight: 700;
    color: #3290ff;
`;

const IdList = styled.div`
    width: 100%;
    height: 130px;
    margin-top: 20px;
    border: none;
    border-radius: 7px;
    background-color: #f2f3f5;
    overflow: scroll;
`;

const ButtonArea = styled.div`
    width: 100%;
    height: 45px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    margin-top: 20px;
`;

const Button = styled.div`
    width: 100%;
    height: 50px;
    border: none;
    background-color: #3290ff;
    border-radius: 8px;
    text-align: center;
    cursor: pointer;
    color: #fff;
    font-size: 18px;
    font-weight: 700;
    line-height: 50px;
`;

const Id = styled.div`
    width: 100%;
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 18px;
    font-weight: 500;
    color: #595959;
    margin-top: 5px;
`;

export default ResultModal;
