import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useRecoilValue } from 'recoil';
import { LoginState } from '../../recoil/Atom';

import TopBar from '../../components/Main/TopBar';
import MainFooter from '../../components/Main/MainFooter';

import check from '../../asset/bannerCheck.png';
import banner from '../../asset/pricingbanner.png';
import vector from '../../asset/Vector.png';

export default function Pricing() {
    const navigate = useNavigate();
    const isLogin = useRecoilValue(LoginState);

    const onClickBasic = () => {
        // if(!isLogin){
        //     alert('로그인 후 이용해주세요.');
        //     navigate('/login');
        //     return;
        // }
        // else{
        //     navigate('/payment/basic');
        // }
        navigate('/payment/basic');
    }

    const onClickEnterprise = () => {
        // if(!isLogin){
        //     alert('로그인 후 이용해주세요.');
        //     navigate('/login');
        //     return;
        // }
        // else{
        //     navigate('/payment/enterprise');
        // }
        navigate('/payment/enterprise');
    }

    return(
        <MainContainer>
            <TopBar />
            <Content>
                <TitleArea>
                    <Title>
                        GLOT AI. 선택하고 변형하여{'\n'}
                        완성하는 텍스트(text)의 작문
                    </Title>
                    <Button
                        onClick={() => {
                            navigate('/writing');
                        }}
                    >
                        무료 체험하기
                        <img 
                            src={vector}
                            alt='vector'
                            style={{
                                width: '18px',
                                height: '15px',
                                marginLeft: '8px',
                            }}
                        />
                    </Button>
                </TitleArea>
                <ContentArea>
                    <Sentence>
                        <div className="highlight">하나</div>, 원하는 문장을 입력하고 GLOT AI와 함께 다음 문장을 이어가요.{'\n'}
                    </Sentence>
                    <Sentence>
                        <div className='highlight'>둘</div>, GLOT AI와 함께 작문하면서 글의 방향에 대하여 코칭을 받아요.{'\n'}
                    </Sentence>
                    <Sentence>
                        <div className='highlight'>셋</div>, 완성된 작문 내용을 PDF로 다운로드를 받아요.
                    </Sentence>
                </ContentArea>
                <ImageArea>
                    <img
                        src={banner}
                        alt='banner'
                        style={{
                            width: '100%',
                            height: '100%',
                            objectFit: 'cover',
                            borderRadius: '30px',
                        }}
                    />
                </ImageArea>
                <TextArea>
                    <Line>
                        GLOT<div className='highlight'>(General Logic of Text)&nbsp;</div> 인공 지능<div className='highlight'>(AI)&nbsp;</div> 1.0{'\n'}
                    </Line>
                    <Bold>더 다양하게 사용하는 방법!</Bold>
                </TextArea>
                <BannerArea>
                    <Banner>
                        <BannerTitle
                            style={{
                                backgroundColor: 'rgba(229, 213, 68, 1)',
                                color: 'rgba(255, 255, 255, 1)'
                            }}
                        >
                            GLOT 게스트
                        </BannerTitle>
                        <BannerPrice>
                            무료
                        </BannerPrice>
                        <BannerContent>
                            <Check src={check} />작문 입력 <Small>(글자 수 제한)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />트래픽에 따른 서비스 이용 속도 제한{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />작문 AI 초급 레벨 제공{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />PDF 다운로드 제공
                        </BannerContent>
                    </Banner>
                    <Banner>
                        <BannerTitle
                            style={{
                                backgroundColor: 'rgba(50, 144, 255, 1)',
                                color: 'rgba(255, 255, 255, 1)'
                            }}
                        >
                            GLOT 베이직
                        </BannerTitle>
                        <BannerPrice>
                            13,000<Small style={{ fontSize: '36px', lineHeight: '36px', marginBottom: '3px'}}>원</Small><Gray>/월</Gray>
                        </BannerPrice>
                        <BannerContent>
                            <Check src={check} />작문 입력 <Small>(글자 수 무제한)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />GLOT AI 작문 코칭 제공{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />작문 내역 메모리 제공 <Small>(최근 100건 보관)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />작문 내역 문서 다운로드 <Small>(DOC, PDF, JPG, TXT)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />작문 내역 인쇄 기능 제공
                        </BannerContent>
                        <ButtonContent>
                            <CheckButton
                                style={{
                                    backgroundColor: 'rgba(50, 144, 255, 1)',
                                }}
                                onClick={onClickBasic}
                            >
                                GLOT 베이직 구매하기
                            </CheckButton>
                        </ButtonContent>
                    </Banner>
                    <Banner>
                        <BannerTitle
                            style={{
                                backgroundColor: 'rgba(255, 102, 102, 1)',
                                color: 'rgba(255, 255, 255, 1)'
                            }}
                        >
                            GLOT 엔터프라이즈
                        </BannerTitle>
                        <BannerPrice>
                            30,000<Small style={{ fontSize: '36px', lineHeight: '36px', marginBottom: '3px'}}>원</Small><Gray>/월, 유저당</Gray>
                        </BannerPrice>
                        <BannerContent>
                            <Bold
                                style={{
                                    fontSize: '20px',
                                }}
                            >
                                Everything in Plus.
                            </Bold>{'\n'}
                            {'\n'}
                            <Check src={check} />교육 기관 가입 요금제{'\n'} 
                            <Small>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(10개 이상의 계정 단체 등록 시 혜택 적용)</Small>{'\n'}
                            {'\n'}
                            {'\n'}
                            <Check src={check} />교육 전용 계정 할인 적용{'\n'}
                            <Small>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(월 기준 20%, 연 기준 30% 혜택 적용 - %는 임의)</Small>
                        </BannerContent>
                        <ButtonContent>
                            <CheckButton
                                style={{
                                    backgroundColor: 'rgba(255, 102, 102, 1)',
                                }}
                                onClick={onClickEnterprise}
                            >
                                GLOT 엔터프라이즈 구매하기
                            </CheckButton>
                        </ButtonContent>
                    </Banner>
                </BannerArea>
            </Content>
            <MainFooter />
        </MainContainer>
    )
}

const MainContainer = styled.div`
    width: 100%;
    height: 100%;
    background-color: #FFFFFF;
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const Content = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid rgba(242, 243, 245, 1);
`;

const TitleArea = styled.div`
    width: 1064px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    margin: 0 auto;
    margin-top: 74px;
`;

const Title = styled.div`
    width: 100%;
    font-size: 48px;
    font-weight: 500;
    line-height: 67.2px;
    letter-spacing: -0.03em;
    text-align: left;
    white-space: pre-line;
