    white-space: pre-line;
`;

const ImageArea = styled.div`
    width: 1064px;
    height: 574px;
    border-radius: 30px;
    background-color: rgba(255, 255, 255, 1);
    margin: 0 auto;
    margin-top: 48px;
`;

