    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;
    font-size: 40px;
    font-weight: 500;
    line-height: 56px;
    letter-spacing: -0.03em;
    color: rgba(17, 17, 17, 1);
    white-space: pre-line;
    margin-top: 82px;
`;

const Line = styled.div`
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: baseline;
    font-size: 40px;
    font-weight: 500;
    line-height: 56px;
    letter-spacing: -0.03em;
    color: rgba(17, 17, 17, 1);
    white-space: pre-line;
    margin-bottom: 24px;
    div.highlight {
        font-size: 32px;
        line-height: 44.8px;
    }
`;

