    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-top: 53px;
    margin-bottom: 96px;
`;

const Banner = styled.div`
    width: 385px;
    height: 560px;
    border-radius: 24px;
    background-color: rgba(255, 255, 255, 1);
    margin: 0 22px;
    box-shadow: 0px 5px 60px 0px rgba(62, 96, 130, 0.16);
`;

