    padding: 24px;
    font-size: 48px;
    font-weight: 600;
    line-height: 48px;
    letter-spacing: -0.03em;
    text-align: center;
    color: rgba(17, 17, 17, 1);
    display: flex;
    justify-content: flex-start;
    align-items: end;
    border-bottom: 1px solid rgba(242, 243, 245, 1);
`;

const BannerContent = styled.div`
    width: calc(100% - 48px);
    height: 244px;
    padding: 24px;
    font-size: 17px;
